mapper.select:
select job_type jobType ,case when job_type = 0 then "SQL流任务" when job_type = 1 then "JAR包" when job_type = 2 then "SQL批任务" end jobTypeName ,runNum from ( SELECT b.job_type, COUNT(*) runNum FROM job_run_log a LEFT JOIN job_config b ON a.job_config_id = b.id WHERE 1 = 1
and a.operator_id = #{operatorId} and b.operator_id = #{operatorId}
GROUP BY b.job_type ) a

select a.curDay ,ifnull(b.jobTotalCount,0) jobTotalCount ,ifnull(b.runSuccessCount,0) runSuccessCount ,ifnull(b.runFailCount,0) runFailCount ,ifnull(b.otherStatusCount,0) otherStatusCount from ( select date_add(curdate(), interval(cast(id as signed) - 29) day) as curDay from mq_serial_num order by id ) as a left join ( select date_format(add_time,"%Y-%m-%d") as curDay ,count(*) as jobTotalCount ,sum( case when job_status = "SUCCESS" then 1 else 0 end ) as runSuccessCount ,sum( case when job_status = "FAIL" then 1 else 0 end) as runFailCount ,sum( case when job_status != "SUCCESS" and job_status != "FAIL" then 1 else 0 end) as otherStatusCount from job_run_log where DATE_FORMAT( add_time, '%Y-%m-%d' ) > DATE_SUB( CURDATE(), INTERVAL 30 DAY )
and operator_id = #{operatorId}
group by date_format(add_time,"%Y-%m-%d") ) as b on a.curDay = b.curDay where 1=1 order by a.curDay

select date_format(now(),"%Y-%m-%d") as curDay ,b.job_type ,count(*) as jobTotalCount ,sum( case when job_status = "SUCCESS" then 1 else 0 end ) as runSuccessCount ,sum( case when job_status = "FAIL" then 1 else 0 end) as runFailCount ,sum( case when job_status != "SUCCESS" then 1 else 0 end) as otherStatusCount from job_run_log a left join job_config b on a.job_config_id = b.id where a.job_status != "RUN" and a.job_status != "STARTING"
and a.operator_id = #{operatorId} and b.operator_id = #{operatorId}
group by date_format(now(),"%Y-%m-%d") ,b.job_type
